mapper.select:
SELECT skill,score 
FROM work_skill 
WHERE user_id = #{user_id}

SELECT category,image 
FROM image_album 
WHERE user_id = #{user_id}

SELECT prj_name,p_content ,used_skill,period_st,period_ex
FROM portfolio 
WHERE user_id = #{user_id}

SELECT r_temp
FROM resume
WEHRE user_id = #{user_id}

